mapper.insert:

  SELECT BLOG_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BLOG_T (
    BLOG_NO
  , TITLE
  , CONTENTS
  , USER_NO
  , IP
  , CREATED_AT
  , MODIFIED_AT
) VALUES( 
    #{blogNo}
  , #{title}
  , #{contents}
  , #{userNo}
  , #{ip}
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH:MI:SS')
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH:MI:SS')
)

INSERT INTO BLOG_IMAGE_T(
    BLOG_NO
  , IMAGE_PATH
  , FILESYSTEM_NAME
) VALUES(
  #{blogNo}
, #{imagePath}
, #{filesystemName}
)
